/*
	_canvas_small // cc teddavis.org 2020
	demo for working with SMALLER canvas than fullscreen
	= set position of small canvas and disable windowResized
*/

function setup() {
	let cvn = createCanvas(300, 300)
	cvn.position(windowWidth / 2 - width / 2, windowHeight / 2 - height / 2)
	background(0) // set initial background to see diff to canvas
	windowResized = null // remove built-in resizing
}

function draw() {
	background(0, 0, 255, 15)
	stroke(255)
	noFill()
	beginShape()
	let lc = 8
	for(let i = 0; i < lc; i++) {
		let x = noise(i*15+frameCount*.01)*width
		let y = noise(i*15+frameCount*.011)*height
		vertex(x, y)
	}
	endShape(CLOSE)
}

/*
	_canvas_poster // cc teddavis.org 2020
	demo for working with BIGGER canvas than fullscreen
	= draw to a layer that is x bigger than screen
*/

let poster, scl = 1.0

function setup() {
	createCanvas(windowWidth, windowHeight)
	poster = createGraphics(2348, 3370) // A0, 72dpi
	scl = (height-100) / poster.height
	imageMode(CENTER)
}

function draw() {
	// draw on poster layer
	poster.reset()
	poster.translate(poster.width / 2, poster.height / 2)
	poster.background(0, 0, 255)
	poster.noFill()
	poster.stroke(255)
	poster.textAlign(CENTER, CENTER)
	let lc = 40;
	for(let i = 0; i < lc; i++) {
		poster.push();
		poster.rotate(radians(map(i, 0, lc - 1, 90, 0)))
		poster.textSize(map(i, 0, lc - 1, poster.width, poster.width / 4))
		poster.strokeWeight(map(i, 0, lc - 1, .001, 4))
		poster.stroke(map(i, 0, lc - 1, 0, 255))
		poster.text("P5LIVE", 0, 0)
		poster.pop()
	}

	// draw poster to screen
	background(0)
	image(poster, width / 2, height / 2, poster.width * scl, poster.height * scl)
}

function keyPressed(){
	// save poster on 'S'
	if(key == 'S'){
		poster.save('poster.png')
	}
}

