/*	
	_audio_analysis // cc teddavis.org 2019
	add snippet to other sketches: CTRL + SHIFT + A 
*/

function setup() {
	createCanvas(windowWidth, windowHeight)

	setupAudio()
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio()

	background(0)
	noFill()
	stroke(255)

	/* average */
	text("MIX", width * .5, height / 4)
	ellipse(width / 2, height / 4, amp)
	text("L", width * .25, height / 4)
	ellipse(width * .25, height / 4, ampStereo.l)
	text("R", width * .75, height / 4)
	ellipse(width * .75, height / 4, ampStereo.r)

	/* waveform */
	beginShape()
	for(let i = 0; i < waveform.length; i++) {
		let freq = waveform[i] * height / 4 // (-1, 1)
		let x = map(i, 0, waveform.length, 0, width)
		curveVertex(x, height / 2 + freq)
	}
	endShape()


	/* fft */
	for(let i = 0; i < fft.length; i++) {
		let freq = fft[i]; // (0, 255)
		let x = map(i, 0, fft.length, 0, width)
		let w = width / fft.length
		rect(x, height, w, -freq)
	}

}

/* AUDIO INIT */
let mic, fftRaw, fft = [],
	waveform = [],
	amp = 0.0,
	ampStereo = {
		l: 0.0,
		r: 0.0
	},
	ampEase = 0.0,
	numBins = 512,
	bands = 12

function setupAudio() {
	userStartAudio()
	mic = new p5.AudioIn()
	mic.start()
	fftRaw = new p5.FFT(0.75, numBins)
	fftRaw.setInput(mic)
}

function updateAudio() {
	fftRaw.analyze()
	amp = mic.getLevel() * 1000 // average mixed amplitude
	ampStereo.l = mic.amplitude.getLevel(0) * 500 // average left amplitude
	ampStereo.r = mic.amplitude.getLevel(1) * 500 // average right amplitude
	ampEase = ease(amp, ampEase, 0.075) // smooth 'amp'
	waveform = fftRaw.waveform() // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)) // array (0, 255)
}

/*	_math_lissajousOrb // cc teddavis.org 2020	*/

function setup() {
	createCanvas(windowWidth, windowHeight)
	background(0)
}

function draw() {

	let xSpeed = 2.1 // rate of left-right
	let ySpeed = 1 // rate of up-down
	let sSpeed = 10 // rate of ellipse

	fill(255)
	stroke(255)
	if(frameCount % 2 === 0) {
		stroke(0)
	}
	strokeWeight(5)

	let oscX = sin(frameCount * 0.01 * xSpeed) * height * .4
	let oscY = cos(frameCount * 0.01 * ySpeed) * height * .4
	let s = sin(frameCount * 0.01 * sSpeed) * 150

	translate(width / 2, height / 2)
	ellipse(oscX, oscY, s, s)
}

