}

function draw() {
	background(0)
	let lc = 20
	fill(255)
	for(let i = 0; i < lc; i++) {
		let s = width / lc
		let x = map(i, 0, lc - 1, 0, width)
		let osc = sin((frameCount * .01) + i * map(mouseX, 0, width, 0, 5))
		let y = height / 2 + osc * abs(mouseY - height / 2)
		stroke(255)
		strokeWeight(3.1 - (osc * 3))
		line(x, y, x, 0)
		noStroke()
		ellipse(x, y, s, s)
	}
}

