function draw() {
	background(0)
	translate(width / 2, height / 2)

	for(let i = 0; i < points.length; i++) {
		let p = points[i]
		let s = mouseY / 10 + sin(i * .25 + frameCount * .05) * 20
		circle(p.x, p.y, s)
	}
}

function keyPressed() {
	genType(key, height / 2)
}

function genType(txtString, txtSize) {
	// grab bounding box of text
	let bounds = font.textBounds(txtString, 0, 0, txtSize)

	// textToPoints(txt, x, y, size, options)
	points = font.textToPoints(txtString, -bounds.w / 2, bounds.h / 2, txtSize, {
		sampleFactor: .07,
		simplifyThreshold: 0
	})
}

/*	_typo_textToPoints-lines // cc teddavis.org 2020	*/

let font, points

function preload() {
	font = loadFont("includes/demos-data/fonts/RobotoMono-Regular.otf")
}

function setup() {
	createCanvas(windowWidth, windowHeight)
	noFill()
	stroke(255)
	strokeWeight(1)

	genType('P5LIVE', width / 4)
}

function draw() {
	background(0)

	translate(width / 2, height / 2)

	beginShape(LINES);
	for(let i = 0; i < points.length; i++) {
		let p = points[i]
		let xScl = sin(i * .031 + frameCount * .021) * sin(frameCount * .01) * map(mouseX, 0, width, .01, .5)
		let yScl = sin(i * .039 + frameCount * .04) * sin(frameCount * .002) * map(mouseY, 0, height, .01, .5)
		let x = map(p.x, width * xScl, width * (1 + xScl), 0, width)
		let y = map(p.y, height * yScl, height * (yScl + 1), 0, height)
		vertex(x, y)
		vertex(p.x, p.y)
	}
	endShape()
}

function keyPressed() {
	genType(key, height / 2)
}

function genType(txtString, txtSize) {
	// grab bounding box of text
	let bounds = font.textBounds(txtString, 0, 0, txtSize)

	// textToPoints(txt, x, y, size, options)
	points = font.textToPoints(txtString, -bounds.w / 2, bounds.h / 2, txtSize, {
		sampleFactor: .07,
		simplifyThreshold: 0
	})
}

/*	
	_typo_opentype // cc teddavis.org 2021	
	isolated glyphs + opentype.js features
*/

let libs = ['https://cdn.jsdelivr.net/npm/opentype.js@latest/dist/opentype.min.js']

let fontPath = 'includes/demos-data/fonts/RobotoMono-Regular.otf'
let path

function setup() {
	createCanvas(windowWidth, windowHeight)
	noFill()

	genOpenType('P5LIVE', width / 4)
}

function draw() {
	background(0, 5)
	translate(width / 2, height / 2)

	let osc = sin(frameCount * .01) * 15

	// cycle path if ready
	if(path) {
		let c = 0
		for(let cmd of path.commands) {
			if(cmd.type === 'M') {
				// start of each glyph
				push()
				rotate(radians(osc))
				beginShape()
				vertex(cmd.x, cmd.y)
			} else if(cmd.type === 'L') {
				vertex(cmd.x, cmd.y)
			} else if(cmd.type === 'C') {
				bezierVertex(cmd.x1, cmd.y1, cmd.x2, cmd.y2, cmd.x, cmd.y)
			} else if(cmd.type === 'Q') {
				quadraticVertex(cmd.x1, cmd.y1, cmd.x, cmd.y)
			} else if(cmd.type === 'Z') {
				// end of each glyph
				stroke((c * 5 + frameCount) % 255);
				endShape(CLOSE)
				pop()
			}
			c++
		}
	}
}

function genOpenType(txtString, txtSize) {
	opentype.load(fontPath, function(err, f) {
		if(err) {
			print('Error loading font: ' + err)
		} else {
			// grab once for measuring
			path = f.getPath(txtString, 0, 0, txtSize)
			let bounds = path.getBoundingBox()

			// reposition to 0, 0 as center (for WEBGL, rotate, etc)
			path = f.getPath(txtString, -(bounds.x2 - bounds.x1) / 2, (bounds.y2 - bounds.y1) / 2, txtSize)
		}
	})
}

