function googleFont(fontName) {
	let link = document.createElement("link")
	link.href = "https://fonts.googleapis.com/css?family=" + encodeURI(fontName) + '&display=swap'
	link.rel = "stylesheet"
	document.head.appendChild(link)
	return fontName
}

/*	_gui_slidersGrid // cc teddavis.org 2019	*/
/*	hide editor to adjust sliders		*/

let border, grid = 40
let sGrid, sModular, sSize, sSpeed, sSizeSpeed, cbSize

function setup() {
	createCanvas(windowWidth, windowHeight)
	noStroke()
	buildGUI()
}

function draw() {
	background(0)

	let c = 0;
	for(let i = 0; i < width; i += grid) {
		for(let j = 0; j < height; j += grid) {
			fill(abs(sin(frameCount * sSpeed.value() / 1000 + c * sModular.value())) * 255)
			let s = grid
			if(cbSize.checked()) {
				s = sin(c * sSpeed.value() / sSizeSpeed.value()) * grid
			}
			ellipse(border + i, border + j, s)
			c++
		}
	}
}

function buildGUI() {
	let x = 15,
		y = height * .05,
		xoff = 25
	let elmBG = createDiv('')
	elmBG.position(x, height * .02)
	elmBG.size(200, y * 4.5)
	elmBG.style('background', '#000')
	elmBG.style('opacity', '.5')
	elmBG.style('border-radius', '20px')

	border = grid / 2
	sGrid = createSlider(20, 100, grid)
	sGrid.position(x + xoff, y)
	sGrid.input(updateGrid)
	setLabel('GRID', x + xoff, y)
	sSpeed = createSlider(2, 100, 10)
	sSpeed.position(x + xoff, y * 2)
	setLabel('SPEED', x + xoff, y * 2)
	sModular = createSlider(0, 100, 11)
	sModular.position(x + xoff, y * 3)
	setLabel('MODULATE', x + xoff, y * 3)
	sSizeSpeed = createSlider(1, 4000, 100)
	sSizeSpeed.position(x + xoff, y * 4)
	setLabel('SIZE OFFSET', x + xoff, y * 4)
	cbSize = createCheckbox('', false)
	cbSize.position(x + 5, y * 4)
}

function setLabel(txt, x, y) {
	let elm = createDiv(txt)
	elm.position(x, y - 12)
	elm.style('color', '#fff')
	elm.style('font-size', '9pt')
	elm.style('font-family', 'sans-serif')
}

function updateGrid() {
	grid = sGrid.value()
	border = grid / 2
}

/*	_libs_p5.glitch // cc teddavis.org 2020	*/
/*	visit https://p5.glitch.me to learn more	*/

let libs = ["includes/libs/p5.glitch.js"]

let glitch, capture, w = 320,
	h = 240

function setup() {
	capture = createCapture(VIDEO)
	capture.size(w, h)
	capture.hide()

	createCanvas(windowWidth, windowHeight)

	background(0)
	imageMode(CENTER)

	glitch = new Glitch(this)
	glitch.pixelate(1)
}

function draw() {
	if(frameCount % 3 === 0) {

		if(!mouseIsPressed) {
			glitch.loadImage(capture)
		}

		// map mouseX to # of randomBytes() + mouseY to limitBytes()
		glitch.limitBytes(map(mouseY, 0, height, 0, 1))
		glitch.randomBytes(map(mouseX, 0, width, 0, 100))
		glitch.buildImage()
	}

	image(glitch.image, width / 2, height / 2, glitch.width, glitch.height)
}

/*	_libs_voronoi // cc teddavis.org 2020 */

let libs = [
	'https://cdn.jsdelivr.net/npm/voronoi@1.0.0/rhill-voronoi-core.js' // remote
	, 'includes/libs/rhill-voronoi-core.js' // load locally (if offline)
	, 'includes/demos-data/js/voronoi-p5live.js'
]

let bbox, voronoi
let sites = [],
	edges = []
let initTypo = true

function setup() {
	createCanvas(windowWidth, windowHeight)
	bbox = {
		xl: 0,
		xr: width,
		yt: 0,
		yb: height
	}
	voronoi = new Voronoi()

	// pre-loading custom points from 'voronoi-p5live.js'
	typoPoints()

	stroke(255)
	strokeWeight(2)

}

function draw() {
	background(0)

	if(mouseIsPressed) {
		if(initTypo) {
			sites = []
			initTypo = false
		}
		addPoints(mouseX, mouseY)
	}

	beginShape(LINES)
	for(var i = 0; i < edges.length; i++) {
		let edge = edges[i]
		vertex(edge.va.x, edge.va.y)
		vertex(edge.vb.x, edge.vb.y)
	}
	endShape()
}

function addPoints(x, y) {
	sites.push({
		x: x,
		y: y
	})
	result = voronoi.compute(sites, bbox)
	edges = result.edges
}

// custom preloaded points interpreter
function typoPoints() {
	for(let t of p5typo) {
		sites.push({
			x: t.x * width,
			y: t.y * height + height / 5
		})
	}
	result = voronoi.compute(sites, bbox)
	edges = result.edges
}

/*	_input_webcam // cc teddavis.org 2020	*/


