	theShader = tex.createShader(vertShader, fragShader)
	tex.noStroke()
}


function draw() {
	// draw shader to texture
	tex.shader(theShader) // apply shader

	// set uniforms
	theShader.setUniform('u_resolution', [width, height])
	theShader.setUniform('u_time', frameCount * .01)

	// set + display shader
	tex.noStroke()
	tex.rect(0, 0, width, height)

	// set shader as texture for 3D primatives
	texture(tex)

	// draw 3D environment
	orbitControl(5)
	translate(sin(frameCount * .004) * width / 3, sin(frameCount * .008) * height / 8)
	rotateX(radians(frameCount / 2))
	sphere(height * .125)
	rotateZ(radians(-frameCount))
	ellipse(0, 0, 400)

}

/* SHADER DEFINITIONS */

let vertShader = `
	//standard vertex shader
	attribute vec3 aPosition;

	void main() {

		// Copy the position data into a vec4, adding 1.0 as the w parameter
		vec4 positionVec4 = vec4(aPosition, 1.0);

		// Scale to make the output fit the canvas
		positionVec4.xy = positionVec4.xy * 2.0 - 1.0; 

		// Send the vertex information on to the fragment shader
		gl_Position = positionVec4;
	}
`


let fragShader = `
	precision mediump float;

	uniform vec2 u_resolution; // This is passed in as a uniform from the sketch.js file
	uniform float u_time;// send time

	void main() {
		// position of the pixel divided by resolution, to get normalized positions on the canvas
		vec2 st = gl_FragCoord.xy/u_resolution.xy; 

		gl_FragColor = vec4(abs(sin(u_time + st.x*2.0)),abs(sin(u_time / st.y)),abs(sin(u_time)),1.0); // R,G,B,A

	}
`

