			col *= 0.0;
		if (uv.y < 0.0 || uv.y > 1.0)
			col *= 0.0;

		col*=1.0-0.65*vec3(clamp((mod(fragCoord.x, 2.0)-1.0)*2.0,0.0,1.0));

	    float comp = smoothstep( 0.1, 0.9, sin(iTime) );

