	//	col = mix( col, oricol, comp );

	    fragColor = vec4(col,1.0);
	}

	//////	END SHADERTOY	//////

	// call shadertoy style above
	void main() {
	  mainImage(gl_FragColor, gl_FragCoord.xy);
