	float nrand( vec2 n ) {
		return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
	}

	vec2 rot2d( vec2 p, float a ) {
		vec2 sc = vec2(sin(a),cos(a));
		return vec2( dot( p, vec2(sc.y, -sc.x) ), dot( p, sc.xy ) );
	}

	const int NUM_TAPS = 27;
